import React from "react";
import {
    Container,
    Row,
    Col,
    Button,
    Modal,
    ListGroup,
    Spinner,
  } from "react-bootstrap";
import "./MainPage.css";
import Select from "react-select";
import queen from "../queen.jpg";
import x from "../x.jpg";

var options = [{ label: "1 queen", value: 1 }];
const colors = {
    1: "#23AF23",
    2: "#98C90B",
  };

  const colors2 = {
    2: "#23AF23",
    1: "#98C90B",
  };

export default class MainPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            board: [],
            board2: [],
            solution: [],
            occupied: new Set(),
            visited: new Set(),
            queens: 8,
            show: false,
            showselected: false,
            curSelectSolution: null,
            loading: "none",
          };
    }

    componentDidMount() {
        for (var j = 2; j <= 32; j++) {
          options.push({ label: `${j} queens`, value: j });
        }
        var curBoard = [];
        for (var i = 0; i < this.state.queens; i++) {
          curBoard.push(new Array(this.state.queens).fill(0));
          this.state.occupied.add(i);
        }
        this.setState({
          board: curBoard,
          board2: JSON.parse(JSON.stringify(curBoard)),
        });
      }

    Queennumber = (e) => {
        const val = e.value;
        var curBoard = [];
        for (var i = 0; i < val; i++) {
          curBoard.push(new Array(val).fill(0));
          this.state.occupied.add(i);
        }
        this.setState({
          board: curBoard,
          board2: JSON.parse(JSON.stringify(curBoard)),
          queens: val,
          visted: new Set(),
          occupied: new Set(),
        });
        this.forceUpdate();
    };

    solve = () => {
        var mysolution = [];
        if (this.solveThePuzzle(this.state.board2, 0, mysolution) === false)
          alert("there is solution for this board");

        const after_filter = [];
        mysolution.forEach((vall) => {
          var contains = true;
          this.state.visited.forEach((val) => {
            //alert(val[0] +"" +val[1]);
            if (vall[val[0]][val[1]] === 0) {
              contains = false;
            }
          });
          if (contains === true) after_filter.push(vall);
        });

        //alert(JSON.stringify(after_filter));
        this.setState({ solution: after_filter, loading: "none"});
        this.forceUpdate();
      };

    addqueen = (e, i, j) => {
    if (
        !this.isSave(this.state.board, i, j) &&
        (this.state.board[i][j] === 0 || this.state.board[i][j] === -1)
    ) {
        alert("this position can not place queen");
        return;
    }
    var curIndex = this.state.board;
    if (curIndex[i][j] === 1) {
        curIndex[i][j] = 0;
        this.setState({ board: curIndex });
        this.state.visited.delete([i, j]);
        this.state.occupied.add(j);
    } else if (curIndex[i][j] === 0 && this.state.queens > 0) {
        curIndex[i][j] = 1;
        this.state.visited.add([i, j]);
        this.setState({ board: curIndex });
        this.state.occupied.delete(j);
    } else {
        alert("There is no queen left");
    }
    this.forceUpdate();
    };

    isSave = (board, row, col) => {
    var i, j;
    for (i = 0; i < board.length; i++) if (board[row][i] === 1) return false;

    for (i = row, j = col; i >= 0 && j >= 0; i--, j--)
        if (board[i][j] === 1) return false;

    for (i = row, j = col; j >= 0 && i < board.length; i++, j--)
        if (board[i][j] === 1) return false;
    // used for function unavaliable
    for (i = row, j = col; i < board.length && j < board.length; i++, j++)
        if (board[i][j] === 1) return false;
    for (i = row, j = col; i >= 0 && j < board.length; i--, j++)
        if (board[i][j] === 1) return false;
    for (i = board.length; i >= 0; i--) if (board[row][i] === 1) return false;
    return true;
    };

    solveThePuzzle = (board, col, mysolution) => {
        if (col === board.length) {
          mysolution.push(JSON.parse(JSON.stringify(board)));
          return true;
        }

        var res = false;
        for (var i = 0; i < board.length; i++) {
          if (this.state.visited.has(col + "" + i + "")) {
            alert(1);
            continue;
          }
          if (this.isSave(board, i, col)) {
            board[i][col] = 1;
            res = this.solveThePuzzle(board, col + 1, mysolution) || res;
            board[i][col] = 0;
          }
        }
        return res;
    };

    render() {
        return (
            <div className='web-container'>
                <div className='mainpart'>
                    <Spinner 
                    animation="border" 
                    role="status"
                    style={{display: 'none'}}>
                        <span className="sr-only">Loading...</span>
                    </Spinner>
                    <div className='sizeDecide'>
                        <div id='selection'>
                            <Select
                                style={{position: 'absolute', top: '50%'}}
                                onChange={(e) => {
                                console.log(e);
                                this.Queennumber(e);
                                }}
                                label="Select Queens"
                                options={options}
                            />
                        </div>
                        <div id='texts'>
                            <p>Backtracking Search for -queeen problem</p>
                        </div>
                    </div>
                    <div style={{backgroundColor:'rgba(0, 0, 0, 0.7)', width: '94%', height: '40%', marginTop: '30px', marginLeft: '3%', border: 'solid #33BCC1 2px'}}>
                        <p style={{marginTop: '10px', marginLeft: '3%' ,height: '20px', color: 'white', fontWeight: 'bold', fontSize: '15px'}}>Specify Any Required location</p>
                        <div className='controller'>
                            {this.state.board.map((val, index) => (
                                <div className='cheese'>
                                    <p style={{backgroundColor: '#33BCC1', width: '30px', height: '100%', color: 'white', fontSize: '15px', textAlign: 'center', borderRadius: '6px', display: 'inline-block'}}>
                                        Q{index+1}:
                                    </p>
                                    {val.map((val, index) => (
                                        <a className='pickrow'>
                                            {index+1}
                                        </a>
                                    ))}
                                </div>
                            ))}
                        </div>
                        <Button id='button' onClick={this.solve}>
                            Slove
                        </Button>
                    </div>
                    <div id='mainboard'>
                    {this.state.board.map((val, index) => {
                        return (
                            <div
                            xs={10}
                            key={"row" + index}
                            style={{
                                height: 640 / this.state.board.length + "px",
                                alignContent: "center",
                                justifyContent: "center",
                                textAlignVertical: "center",
                                textAlign: "center",
                                margin: "0.1vh"
                            }}
                            >
                            {val.map((vall, a) => {
                                const cur = index % 2 === 0 ? colors2 : colors;
                                var img = "";
                                switch (vall) {
                                default:
                                    img = "";
                                    break;
                                case 1:
                                    img = queen;
                                    break;
                                case -1:
                                    img = x;
                                    break;
                                }
                                return (
                                <div
                                    style={{
                                    width: 640/val.length +'px',
                                    display: "inline-block",
                                    alignContent: "center",
                                    justifyContent: "center",
                                    textAlignVertical: "center",
                                    textAlign: "center",
                                    backgroundColor: cur[(a % 2) + 1],
                                    margin: "0.1vh",
                                    height: "100%"
                                    }}
                                >
                                    <Button
                                    style={{
                                        backgroundColor: "transparent",
                                        outlineColor: "transparent",
                                        borderColor: "transparent",
                                        marginTop: "10%",
                                        backgroundImage: `url(${img})`,
                                        backgroundSize: "100% 100%",
                                        height: "80%",
                                        width: "80%"
                                    }}
                                    onClick={(e) => {
                                        this.addqueen(e, index, a);
                                    }}
                                    ></Button>
                                </div>
                                );
                            })}
                            </div>
                        );
                        })}
                    </div>
                </div>
            </div>
        );
    }
}
