import React from "react";
import {
    Container,
    Row,
    Col,
    Button,
    Modal,
    ListGroup,
    Spinner,
  } from "react-bootstrap";
import "./MainPage.css";
import Select from "react-select";

var options = [{ label: "1 queen", value: 1 }];

export default class MainPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            board: [],
            board2: [],
            solution: [],
            occupied: new Set(),
            visited: new Set(),
            queens: 8,
            show: false,
            showselected: false,
            curSelectSolution: null,
            loading: "none",
          };
    }

    componentDidMount() {
        for (var j = 2; j <= 32; j++) {
          options.push({ label: `${j} queens`, value: j });
        }
        var curBoard = [];
        for (var i = 0; i < this.state.queens; i++) {
          curBoard.push(new Array(this.state.queens).fill(0));
          this.state.occupied.add(i);
        }
        this.setState({
          board: curBoard,
          board2: JSON.parse(JSON.stringify(curBoard)),
        });
      }

    Queennumber = (e) => {
        const val = e.value;
        var curBoard = [];
        for (var i = 0; i < val; i++) {
          curBoard.push(new Array(val).fill(0));
          this.state.occupied.add(i);
        }
        this.setState({
          board: curBoard,
          board2: JSON.parse(JSON.stringify(curBoard)),
          queens: val,
          visted: new Set(),
          occupied: new Set(),
        });
        this.forceUpdate();
      };

    render() {
        var cheeses = []
        return (
            <div className='web-container'>
                <div className='mainpart'>
                    <Spinner 
                    animation="border" 
                    role="status"
                    style={{display: 'none'}}>
                        <span className="sr-only">Loading...</span>
                    </Spinner>
                    <div className='sizeDecide'>
                        <div id='selection'>
                            <Select
                                style={{position: 'absolute', top: '50%'}}
                                onChange={(e) => {
                                console.log(e);
                                this.Queennumber(e);
                                }}
                                label="Select Queens"
                                options={options}
                            />
                        </div>
                        <div id='texts'>
                            <p>Backtracking Search for -queeen problem</p>
                        </div>
                    </div>
                    <div style={{backgroundColor:'rgba(0, 0, 0, 0.7)', width: '94%', height: '40%', marginTop: '30px', marginLeft: '3%', border: 'solid aqua 2px'}}>
                        <p style={{backgroundColor: 'green', marginTop: '10px', marginLeft: '3%' ,height: '20px', color: 'white', fontWeight: 'bold', fontSize: '15px'}}>Specify Any Required location</p>
                        <div className='controller'>
                            {cheeses.map((post, index) => (
                                <div className='cheese'>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
